$values = array();
$LowerArr = range('a', 'z') ;
$upperArr = range('A', 'Z') ;
$i = 1;
$group = array();
$g = 0;
foreach($input as $s ){
    $group[$g][] = $s;    
    if($i>2){
        $i = 0;
        $g++;
    }
    $i++;  
}
foreach($group as $single_group){
    $chars = str_split($single_group[0]);
    foreach($chars as $char){
        if(str_contains($single_group[1], $char) && str_contains($single_group[2], $char)){
            if(ctype_upper($char)){
                $value = (array_search($char, $upperArr) + 27); 
            }else{
                $value = (array_search($char, $LowerArr) + 1); 
            }
            $badge[] = $value;
            break;
        }
    }
}
$sum = 0;
foreach($badge as $b){
    $sum = $sum + $b;
}
