<?php 
$input = "";
$chars = str_split($input);
$characters = array(0=> "",1=> "",2=> "",3=> "");
$i = 0;
$count = 0;
foreach($chars as $char){
    if($i>3){
        $i = 0;
    }
    $other_characters = $characters;
    unset($other_characters[$i]);
    $characters[$i] = $char;
    
    $i++;
    $count++;

    
    if($count>3 && array_unique($characters)==$characters){
        echo $count;
        break;
    }
}